def format_name(name):
    try:
        # Split the input string into first name and last name
        first_name, last_name = name.split()
        
        # Format the last name with first letter capitalized followed by a comma
        formatted_name = f"{last_name.capitalize()}, {first_name[0].capitalize()}."
        
        return formatted_name
    except (IndexError, ValueError):
        return "Invalid input format. Please provide first name and last name separated by a space."

